import * as S from "./ShelterMap.style";
import React, { useEffect, useState } from "react";
import { useShelter } from "../../hooks";
import { Modal } from "../shelterlist/mapModal";
// import { Link } from "react-router-dom";
const { kakao } = window;

export const ShelterMap = () => {
  //Îç∞Ïù¥ÌÑ∞ Î∞õÏïÑÏò§Í∏∞
  const { shelter } = useShelter();
  const [modalOpen, setModalOpen] = useState(false);

  useEffect(() => {
    const mapContainer = document.getElementById("map"); // ÏßÄÎèÑÎ•º ÌëúÏãúÌï† div
    const mapOption = {
      center: new kakao.maps.LatLng(37.550229, 126.977279), // ÏßÄÎèÑÏùò Ï§ëÏã¨Ï¢åÌëú=ÏûÑÏãúÎ°ú ÎÇ®ÏÇ∞ÌÉÄÏõå
      level: 5, // ÏßÄÎèÑÏùò ÌôïÎåÄ Î†àÎ≤®
    };
    // ÏßÄÎèÑÎ•º ÌëúÏãúÌï† divÏôÄ ÏßÄÎèÑ ÏòµÏÖòÏúºÎ°ú  ÏßÄÎèÑÎ•º ÏÉùÏÑ±
    const map = new kakao.maps.Map(mapContainer, mapOption);

    // ÎßàÏª§
    const positions = shelter.map((shelter) => {
      return {
        title: shelter.shelterNm,
        type: shelter.shelterType,
        address: shelter.address,
        qty: shelter.qty,
        latlng: new kakao.maps.LatLng(shelter.yCord, shelter.xCord),
      };
    });

    // ÎßàÏª§ Ïù¥ÎØ∏ÏßÄÏùò Ïù¥ÎØ∏ÏßÄ Ï£ºÏÜåÏûÖÎãàÎã§
    const imageSrc = "https://t1.daumcdn.net/localimg/localimages/07/mapapidoc/markerStar.png";

    for (var i = 0; i < positions.length; i++) {
      // ÎßàÏª§ Ïù¥ÎØ∏ÏßÄÏùò Ïù¥ÎØ∏ÏßÄ ÌÅ¨Í∏∞ ÏûÖÎãàÎã§
      const imageSize = new kakao.maps.Size(24, 35);

      // ÎßàÏª§ Ïù¥ÎØ∏ÏßÄÎ•º ÏÉùÏÑ±Ìï©ÎãàÎã§
      const markerImage = new kakao.maps.MarkerImage(imageSrc, imageSize);

      const markerParams = {
        map: map, // ÎßàÏª§Î•º ÌëúÏãúÌï† ÏßÄÎèÑ
        position: positions[i].latlng, // ÎßàÏª§Î•º ÌëúÏãúÌï† ÏúÑÏπò
        title: positions[i].title, // ÎßàÏª§Ïùò ÌÉÄÏù¥ÌãÄ, ÎßàÏª§Ïóê ÎßàÏö∞Ïä§Î•º Ïò¨Î¶¨Î©¥ ÌÉÄÏù¥ÌãÄÏù¥ ÌëúÏãúÎê©ÎãàÎã§
        type: positions[i].type,
        address: positions[i].address,
        qty: positions[i].qty,
        image: markerImage, // ÎßàÏª§ Ïù¥ÎØ∏ÏßÄ
        clickable: true,
      };
      // ÎßàÏª§Î•º ÏÉùÏÑ±Ìï©ÎãàÎã§
      const marker = new kakao.maps.Marker(markerParams);
      // ÎßàÏª§Î•º ÏßÄÎèÑÏóê ÌëúÏãúÌï©ÎãàÎã§.
      marker.setMap(map);

      //üöÄüöÄüöÄüöÄüöÄ ÎßàÏª§Î•º ÌÅ¥Î¶≠ Ïãú Ïù∏Ìè¨ÏúàÎèÑÏö∞
      const iwContent = positions[i].title,
        // Ïù∏Ìè¨ÏúàÎèÑÏö∞Ïóê ÌëúÏ∂úÎê† ÎÇ¥Ïö©ÏúºÎ°ú HTML Î¨∏ÏûêÏó¥Ïù¥ÎÇò document elementÍ∞Ä Í∞ÄÎä•Ìï©ÎãàÎã§
        iwRemoveable = true; // removeable ÏÜçÏÑ±ÏùÑ ture Î°ú ÏÑ§Ï†ïÌïòÎ©¥ Ïù∏Ìè¨ÏúàÎèÑÏö∞Î•º Îã´ÏùÑ Ïàò ÏûàÎäî xÎ≤ÑÌäºÏù¥ ÌëúÏãúÎê©ÎãàÎã§
      // Ïù∏Ìè¨ÏúàÎèÑÏö∞Î•º ÏÉùÏÑ±Ìï©ÎãàÎã§
      const infowindow = new kakao.maps.InfoWindow({
        content: iwContent,
        removable: iwRemoveable,
      });

      // ÎßàÏª§Ïóê ÌÅ¥Î¶≠Ïù¥Î≤§Ìä∏Î•º Îì±Î°ùÌï©ÎãàÎã§
      kakao.maps.event.addListener(marker, "click", function () {
        setModalOpen({
          open: true,
          data: { marker, markerParams },
        });
        infowindow.open(map, marker);
      });
    }
  }, [shelter]);

  return (
    <React.Fragment>
      <S.Map id="map" />
      {modalOpen.open && <Modal mapName={modalOpen.data} setModalOpen={setModalOpen} />}
    </React.Fragment>
  );
};
